mapper.select:
select id, name
from t_type
where id = #{id};

select id, nickname, avatar,type
from t_user
where id = #{id};

select id, title, content, first_picture, description, flag, views, appreciation, share_statement, commentable, recommend, published, create_time, update_time, type_id from t_blog
and title like concat('%', #{title}, '%')
and type_id = #{typeId}
and recommend = #{recommend}
and published = #{published}

mapper.update:
update t_blog
set views = views + 1
where id = #{id}
